import axios from 'axios';

export default {
  name: 'HelloWorld',

  data() {
    return {
      isLoading: false,
      introText: 'Push the button',
      data: [],
      errors: [],
      freeQuery: 'price=0.0',
      aloneQuery: 'participants=1',
      groupQuery: 'minparticipants=3',
      filters: {
        'alone': {
          'name': 'alone',
          'checked': false,
          'query': ''
        },
        'group': {
          'name': 'group',
          'checked': false,
          'query': ''
        },
        'free': {
          'name': 'free',
          'checked': false,
          'query': ''
        }
      }
    }
  },

  computed: {
    getactivity() {
      if (this.data.activity) {
        return this.data.activity
      }

      return this.introText;
    },

    getAloneQuery() {
      if(this.filters.alone.checked){
        return this.aloneQuery;
      }

      return '';
    },

    getFreeQuery() {
      if(this.filters.free.checked){
        return this.freeQuery;
      }

      return '';
    },

    getGroupQuery() {
      if(this.filters.group.checked){
        return this.groupQuery;
      }

      return '';
    }
  },

  methods: {
    fetch() {
      this.loading = true;
      axios.get(`http://www.boredapi.com/api/activity?${this.getAloneQuery}&${this.getFreeQuery}&${this.getGroupQuery}`)
        .then(response => {
          this.data = response.data
          this.loading = false;

        })
        .catch(e => {
          this.errors.push(e)
        });
    },

    onChange($event) {
      const target = $event.target.name;
      const checked = $event.target.checked

      if (checked) {
        for (const filter in this.filters) {
          if (filter === target) {
            console.log(this.filters[filter].checked);
            this.filters[filter].checked = true;

            console.log(this.filters[filter].query);
          }
        }
      } else {
        for (const filter in this.filters) {
          if (filter === target) {
            this.filters[filter].checked = false;
          }
        }
      }
    }
  }
}
